  addWhereClause(CHDE_CYCL_MEAS_DATA::HEATID,HEATID);
  addWhereClause(CHDE_CYCL_MEAS_DATA::TREATID,TREATID);
  addWhereClause(CHDE_CYCL_MEAS_DATA::PLANT,PLANT);
   
