bool CPDH_HM_DATA::doAnnouncement(const std::string& HM_ID, const std::string& TREATID, const std::string& PLANT, sDate &pTreatStartOper,sDate &pTreatEndOper, long HMCounter,const std::string& PRODORDERID, long LADLEFREEBOARD, double HMWEIGHT, double SLAGWEIGHT,double HMTEMP, double SAIM,long CALCMODE, long TREATTYPE, double SACT)
{
  bool retValue;
  CDateTime now;
  //std::string now = time.toDBString();
  int i=1;  // place-holder variable - delete later
  double j=2; // place-holder variable - delete later

  //sankar
  int PLANTNO = 1;
  CShiftCrewData            ShiftCrewData           (m_pCBS_StdConnection);
  CPD_HM_REF            PD_HM_REF           (m_pCBS_StdConnection);
  if(PD_HM_REF.select(HM_ID,CSMC_DBData::unspecString))
  {
	  PLANTNO = PD_HM_REF.getPLANTNO(1);
  }


  std::string CREW_ID;
  std::string SHIFT_ID;
  ShiftCrewData.getCrewAndShiftByDate(CREW_ID, SHIFT_ID, now,PLANT, PLANTNO);

  //SANKAR
CDateTime treatEnd(pTreatEndOper.year, pTreatEndOper.month, pTreatEndOper.day, 
pTreatEndOper.hour, pTreatEndOper.minute, pTreatEndOper.second,pTreatEndOper.msec,pTreatEndOper.dstFlag);

CDateTime treatStart(pTreatStartOper.year, pTreatStartOper.month, pTreatStartOper.day, 
pTreatStartOper.hour, pTreatStartOper.minute, pTreatStartOper.second,pTreatStartOper.msec,pTreatStartOper.dstFlag);

//sankar
		CPP_HM            m_PP_HM(m_pCBS_StdConnection);
		string CustID = CSMC_DBData::unspecString;
    if ( m_PP_HM.select(HM_ID) )
    {
      CustID =  m_PP_HM.getHM_ID_CUST_PLAN(1);
    }

  try
  {
    setHM_ID(HM_ID);
    setTREATID(TREATID);
    setPLANT(PLANT);
    setHOTMETAL_COUNTER(HMCounter);
    setHEATID_CUST(CustID); 
    setPRODORDERID_PLAN(PRODORDERID);
		setPRODORDERID_ACT(PRODORDERID);
    setSTEELGRADECODE_PLAN(" "); // TO BE DONE -- from table PP_ORDER.STEELGRADECODE (where PRODORDERID == table PP_HM.PRODORDERID)
    setHM_ANNOUNCE_ACT(now);
    setTREATSTART_PLAN(treatStart); // TO BE DONE -- from table PP_HM.TREATSTART_PLAN (if data existing)
    setTREATEND_PLAN(treatEnd); // TO BE DONE -- from table PP_HM. TREATEND_PLAN (if data existing)
	setTREATEND_MOD(treatEnd); //SANKAR
    setMAT_CODE_CARBIDE("1010000000"); // TO BE DONE -- from table
    setMAT_CODE_LIME("1020000000"); // TO BE DONE -- from table
    setMAT_CODE_MG("1030000000"); // TO BE DONE -- from table
    setSHIFT_CODE(SHIFT_ID); // TO BE DONE -- from table
    setCREW_CODE(CREW_ID); // TO BE DONE -- from table
    setUSERCODE("HMI"); // TO BE DONE -- from table
    setLADLE_FREEBOARD_BF(LADLEFREEBOARD);
    setHM_WEIGHT_START(HMWEIGHT); // TO BE DONE -- from table
    setHM_WEIGHTSTART_MOD(HMWEIGHT);
    setSLAG_WEIGHT_START(SLAGWEIGHT); // TO BE DONE -- from table
    setSLAG_WEIGHT_START_OPER(SLAGWEIGHT);
    setHM_TEMP_START(HMTEMP); // TO BE DONE -- from table PD_HOT_METAL.SMAPLE_REF (where HOTMETAL_COUNTER == table PDH_HM_DATA.HOTMETAL_COUNTER) -> table PD_SAMPLE_REF.HOT_METAL -> table PD_SAMPLE_ENTRY.SAMPLE_COUNTER -> table PD_SAMPLE_ENTRY.MEASVALUE (where = table PD_SAMPLE_ENTRY.MEASNAME = ‘Temp’)
    setTEMP_INITIAL(HMTEMP);
    setAIM_S_GRADE(SACT); // TO BE DONE -- from table
    setAIM_S_OPER(SAIM); // Sact is also coming from HMI???????
    setCALC_MODE(CALCMODE);
    setTREAT_TYPE_OPER(TREATTYPE);
    setREVTIME(now);
	//sankar

	setHEAT_RESPONSIBILITY(ShiftCrewData.getMemberIdByRole(now, PLANT, PLANTNO, DEF_GC_MEMBER_ROLE_CAT::Melter));
    setCREW_RESPONSIBILITY(ShiftCrewData.getMemberIdByRole(now, PLANT, PLANTNO, DEF_GC_MEMBER_ROLE_CAT::Supervisor));


    retValue = insert();
    // commit only if own values are successfully written
    if (retValue)
    {
      commit();
    }
    else
    {
      rollback();
      log("ERROR: CPDH_HM_DATA::doAnnouncement failed",1);
    }
  }
  catch(...)
  {
    retValue = false;
  }

  return retValue;
}



