bool CPDH_HM_DATA::selectFinishedHeat(bool ASC /* = true*/)
{
  cleanWhereStatement();

  m_Statement = "Select * from " + m_TableName;

  addWhereClause(CPDH_HM_DATA::HM_DEPARTURE_ACT + " is not NULL");

  if ( ASC )
  {
    m_Statement += getWhereStatement() + " ORDER BY HM_DEPARTURE_ACT ;";
  }
  else
  {
    m_Statement += getWhereStatement() + " ORDER BY HM_DEPARTURE_ACT DESC;";
  }


  return CSMC_DBData::select();
}
