bool CPDL_HEAT_DATA::UpdateElecConsTotal(const std::string& HEATID, const std::string& TREATID, const std::string& PLANT, const double dbElecConsTotal, const double specEgy, bool Commit, cCBS_ODBC_DBError &Error)
{
	bool result = true;

	if ( select(HEATID, TREATID, PLANT) )
	{
    setELEC_CONS(dbElecConsTotal);
    setSPECEGY(specEgy);

    result = result && update();

		if (!result)
			Error = getLastError();
		
		if (Commit)
		{
			if (result)
				commit();
			else
				rollback();
		}
	}
	return result;
}

bool CPDL_HEAT_DATA::UpdateSlagFormer(const std::string& HEATID, const std::string& TREATID,const std::string& PLANT, const double dbSlagFormer ,bool Commit, cCBS_ODBC_DBError &Error)
{
	std::stringstream sql;
	bool result = true;
	if ( exists(HEATID, TREATID, PLANT)) //item does not exists -> INSERT IT
	{
		sql << "update PDL_HEAT_DATA set SLAG_FORMER = "
			<< dbSlagFormer
			<< "  where HEATID = '" << HEATID << "' and TREATID='" << TREATID << "' and PLANT = '" << PLANT << "'";
		
		result = executeSQL(sql.str());
		if (!result)
			Error = getLastError();
		
		if (Commit)
		{
			if (result)
				commit();
			else
				rollback();
		}
	}
	return result;
}


