bool CPDL_PHASE_STIRRING_RES::writeResults(const std::string &HeatID, const std::string &TreatID, const string& PLANT, CDateTime HeatAnnounceTime, const seqLFStirrPredRes & LFStirrPredRes)
{
  bool            RetValue   = false;

  CDateTime       Now;
  CDateTime       EndPhase;

  sLFStirrPredRes StirrPredRes;

  try
  {
    CGCL_STIRR_PHASE     GCL_STIRR_PHASE   (m_pCBS_StdConnection);

    long SeqLength = CIntfData::getLength(LFStirrPredRes);

    RetValue = !exists(HeatID, TreatID, PLANT);

     //some data found -> delete them
    if (!RetValue && SeqLength > 0 )
    {
      RetValue = deleteRows();
      if (!RetValue)
        setLastError(std::string("ERROR_DELETE_DATA"), 0, getActStatement());
    }

    for (int i = 0 ; i < SeqLength; ++i)
    {
      CIntfData::getAt(StirrPredRes,&LFStirrPredRes,i);

      std::string  StirrPhase = StirrPredRes.StirrPhase;

      if (StirrPredRes.isPatternOrActivity==1) continue; 
      if (StirrPredRes.DurStep <= 0. )         continue; // do not store data in case of invalid duration of step

      if ( GCL_STIRR_PHASE.selectByPracPhaseModel(StirrPhase) )
      {
        StirrPhase = GCL_STIRR_PHASE.getPRACPHASE(1);  
      }

      setHEATID(HeatID);
      setTREATID(TreatID);
      setPLANT(PLANT);
      setSTIRRPHASE(StirrPhase);

      setSTIRRGASTYPE(std::string(StirrPredRes.GasType));
      setINTENSITY(StirrPredRes.StirrIntensity);
      setFLOW(StirrPredRes.StirrFlow);  

      setSTEPDUR(StirrPredRes.DurStep);

      setSTARTTIME(StirrPredRes.DurUntilStartOfStep);
      setDURUNTENDOFPHASE(CDataConversion::RoundToLong(StirrPredRes.DurUntilEndOfStep));

      EndPhase = HeatAnnounceTime;
      EndPhase.addTime(StirrPredRes.DurUntilEndOfStep * 60.0);

      setDATEUNTENDOFPHASE(EndPhase);

      setELEC_ENERGY(StirrPredRes.ElecEgy);

      setREVTIME(Now);

      if (select(HeatID, TreatID, PLANT, StirrPhase))
        RetValue = update();
      else
        RetValue = insert();

      if (!RetValue)
        break;    
    }

    // commit only if own values  are successfully written
    if (RetValue)
    {
      commit();
    }
    else
    {
      rollback();
    }
  }
  catch(...)
  {
    RetValue = false;
    rollback();
  }
  return RetValue;
}


